import React, { useEffect, useMemo, useState } from 'react';
import { Card, CardContent, CardTitle } from '../components/Card';
import { MagnifyingGlassIcon, MapPinIcon, StarIcon } from '@heroicons/react/24/outline';
import { getDoctors, bookAppointment } from '../utils/api';
import { useToast } from '../components/Toast';
import { isAuthenticated } from '../utils/auth';
import { Button } from '../components/Button';

const DoctorFinder = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [specialty, setSpecialty] = useState('');
  const [loading, setLoading] = useState(true);
  const [doctors, setDoctors] = useState([]);
  const { addToast } = useToast();
  const [booking, setBooking] = useState({ open: false, doctor: null, date: '', reason: '', saving: false });

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const list = await getDoctors();
        if (mounted) setDoctors(list || []);
      } catch (e) {
        addToast({ title: 'Failed to load doctors', description: e.message, variant: 'error' });
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => { mounted = false; };
  }, [addToast]);

  const filteredDoctors = useMemo(() => {
    const term = searchTerm.trim().toLowerCase();
    const spec = specialty.trim().toLowerCase();
    return doctors.filter((d) => {
      const matchesTerm = !term || d.name.toLowerCase().includes(term);
      const matchesSpec = !spec || d.specialty.toLowerCase().includes(spec);
      return matchesTerm && matchesSpec;
    });
  }, [doctors, searchTerm, specialty]);

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-2">Find a Doctor</h1>
        <p className="text-gray-600">Search by name and filter by specialty</p>
      </div>

      <Card className="mb-8">
        <CardContent className="grid grid-cols-1 md:grid-cols-3 gap-4 items-end">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Search by name</label>
            <div className="relative">
              <MagnifyingGlassIcon className="h-5 w-5 text-gray-400 absolute left-3 top-1/2 -translate-y-1/2" />
              <input
                type="text"
                className="w-full pl-10 pr-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
                placeholder="e.g. Sarah"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Specialty</label>
            <input
              type="text"
              className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
              placeholder="e.g. Cardiology"
              value={specialty}
              onChange={(e) => setSpecialty(e.target.value)}
            />
          </div>
          <div>
            <button
              type="button"
              className="w-full bg-primary-500 text-white px-4 py-2 rounded-lg hover:bg-primary-600 transition-colors"
              onClick={() => {
                setSearchTerm(searchTerm.trim());
                setSpecialty(specialty.trim());
              }}
            >
              Apply Filters
            </button>
          </div>
        </CardContent>
      </Card>

      {loading ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {Array.from({ length: 6 }).map((_, i) => (
            <div key={i} className="animate-pulse rounded-xl bg-white p-6 shadow-md">
              <div className="flex items-center gap-4">
                <div className="h-20 w-20 rounded-full bg-gray-200" />
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-gray-200 rounded w-1/2" />
                  <div className="h-3 bg-gray-200 rounded w-1/3" />
                  <div className="h-3 bg-gray-200 rounded w-1/4" />
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : filteredDoctors.length === 0 ? (
        <Card>
          <CardContent className="text-center py-10">
            <p className="text-gray-600">No doctors found. Try adjusting your filters.</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredDoctors.map((doc) => (
          <Card key={doc.id}>
            <CardContent>
              <div className="flex items-center gap-4">
                <img
                  src={doc.images && doc.images[0] ? doc.images[0] : 'https://images.unsplash.com/photo-1537368910025-700350fe46c7?q=80&w=400&auto=format&fit=crop'}
                  alt={doc.name}
                  className="h-20 w-20 rounded-full object-cover"
                />
                <div className="min-w-0">
                  <CardTitle className="truncate">{doc.name}</CardTitle>
                  <div className="text-sm text-gray-600">{doc.specialty}</div>
                  <div className="flex items-center gap-2 text-sm text-gray-700 mt-1">
                    <StarIcon className="h-5 w-5 text-yellow-500" />
                    <span>{Number(doc.rating || 0).toFixed(1)}</span>
                    <span className="text-gray-400">â€¢</span>
                    <span>{doc.experience} yrs</span>
                  </div>
                  <div className="flex items-center gap-2 text-sm text-gray-600 mt-1">
                    <MapPinIcon className="h-5 w-5 text-gray-400" />
                    <span className="truncate">{doc.location}</span>
                  </div>
                </div>
              </div>
              <div className="mt-4 flex justify-end">
                <Button
                  onClick={() => {
                    if (!isAuthenticated()) {
                      addToast({ title: 'Login required', description: 'Please log in to book an appointment', variant: 'error' });
                      return;
                    }
                    setBooking({ open: true, doctor: doc, date: '', reason: '', saving: false });
                  }}
                >Book appointment</Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
      )}

      {booking.open && (
        <div className="fixed inset-0 z-50 flex items-center justify-center p-4">
          <div className="absolute inset-0 bg-black/40" onClick={() => setBooking({ open: false, doctor: null, date: '', reason: '', saving: false })} />
          <div className="relative w-full max-w-md rounded-xl bg-white shadow-xl">
            <div className="px-6 py-4 border-b">
              <h3 className="text-lg font-semibold text-gray-800">Book with {booking.doctor?.name}</h3>
              <div className="text-sm text-gray-600">{booking.doctor?.specialty}</div>
            </div>
            <div className="px-6 py-4 space-y-3">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Date</label>
                <input type="date" className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500" value={booking.date} onChange={(e)=>setBooking(b=>({ ...b, date: e.target.value }))} />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Reason</label>
                <input type="text" className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-primary-500" placeholder="Optional" value={booking.reason} onChange={(e)=>setBooking(b=>({ ...b, reason: e.target.value }))} />
              </div>
            </div>
            <div className="px-6 py-4 border-t flex gap-3 justify-end">
              <Button variant="secondary" onClick={() => setBooking({ open: false, doctor: null, date: '', reason: '', saving: false })}>Cancel</Button>
              <Button
                loading={booking.saving}
                onClick={async () => {
                  if (!booking.date) {
                    addToast({ title: 'Date required', variant: 'error' });
                    return;
                  }
                  try {
                    setBooking(b=>({ ...b, saving: true }));
                    await bookAppointment({ doctorId: booking.doctor.id, date: booking.date, reason: booking.reason });
                    addToast({ title: 'Appointment booked', variant: 'success' });
                    setBooking({ open: false, doctor: null, date: '', reason: '', saving: false });
                  } catch (e) {
                    addToast({ title: 'Booking failed', description: e.message, variant: 'error' });
                    setBooking(b=>({ ...b, saving: false }));
                  }
                }}
              >Confirm</Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default DoctorFinder;
