import React, { useEffect, useRef, useState } from 'react';
import { Card, CardContent, CardTitle } from '../components/Card';
import { Button } from '../components/Button';
import { healthAssistant } from '../utils/api';
import { useToast } from '../components/Toast';

const VoiceControl = () => {
  const [text, setText] = useState('');
  const [error, setError] = useState('');
  const { addToast } = useToast();
  const [dictating, setDictating] = useState(false);
  const recognitionRef = useRef(null);
  const [chat, setChat] = useState([]); // {role:'user'|'assistant', text}
  const [conversationMode, setConversationMode] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [voices, setVoices] = useState([]);
  const [selectedVoice, setSelectedVoice] = useState(null);

  useEffect(() => {
    // Load available voices
    const loadVoices = () => {
      const availableVoices = speechSynthesis.getVoices();
      setVoices(availableVoices);
      // Select a default voice (prefer female, English)
      const defaultVoice = availableVoices.find(v => v.lang.startsWith('en') && v.name.toLowerCase().includes('female')) ||
                          availableVoices.find(v => v.lang.startsWith('en')) ||
                          availableVoices[0];
      setSelectedVoice(defaultVoice);
    };

    loadVoices();
    if (speechSynthesis.onvoiceschanged !== undefined) {
      speechSynthesis.onvoiceschanged = loadVoices;
    }

    const SR = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SR) {
      const r = new SR();
      r.lang = 'en-US';
      r.continuous = false;
      r.interimResults = false;
      r.onresult = (e) => {
        const transcript = e.results[0][0].transcript;
        setText(transcript);
        if (conversationMode && transcript.trim()) {
          handleUserMessage(transcript.trim());
        }
      };
      r.onend = () => {
        setDictating(false);
        setIsListening(false);
        if (conversationMode) {
          // Auto-restart listening after a short delay
          setTimeout(() => startListening(), 1000);
        }
      };
      r.onerror = (e) => {
        console.error('Speech recognition error:', e.error);
        setDictating(false);
        setIsListening(false);
        setError('Voice recognition failed. Please try again.');
      };
      recognitionRef.current = r;
    }
  }, [conversationMode]);



  const startListening = () => {
    setError('');
    if (!recognitionRef.current) {
      setError('Voice recognition not supported in this browser');
      return;
    }
    try {
      recognitionRef.current.start();
      setDictating(true);
      setIsListening(true);
    } catch (e) {
      setError('Could not start voice recognition');
    }
  };

  const stopListening = () => {
    try {
      recognitionRef.current && recognitionRef.current.stop();
    } catch (_) {}
    setDictating(false);
    setIsListening(false);
  };

  const speak = (msg, langHint) => {
    try {
      const utter = new SpeechSynthesisUtterance(msg);
      if (selectedVoice) {
        utter.voice = selectedVoice;
      } else if (langHint && langHint.startsWith('hi')) {
        utter.lang = 'hi-IN';
      }
      utter.rate = 0.9; // Slightly slower for clarity
      utter.pitch = 1.0;
      window.speechSynthesis.speak(utter);
    } catch (_) {}
  };

  const handleUserMessage = async (message) => {
    const language = /[\u0900-\u097F]/.test(message) ? 'hi' : 'en';
    const userMsg = { role: 'user', text: message };
    setChat((c) => [...c, userMsg]);

    try {
      const { reply } = await healthAssistant({ message, language });
      const assistantMsg = { role: 'assistant', text: reply || 'I\'m here to help. Please describe your symptoms.' };
      setChat((c) => [...c, assistantMsg]);
      speak(assistantMsg.text, language);
    } catch (e) {
      const assistantMsg = { role: 'assistant', text: 'I\'m sorry, I\'m having trouble right now. Please try again or consult a doctor for medical advice.' };
      setChat((c) => [...c, assistantMsg]);
      speak(assistantMsg.text, 'en');
    }
  };

  const toggleConversationMode = () => {
    if (conversationMode) {
      stopListening();
      setConversationMode(false);
      speak('Voice conversation ended. You can still type your questions below.', 'en');
    } else {
      setConversationMode(true);
      setChat([]);
      speak('Starting voice conversation. Tell me about your symptoms and I\'ll help guide you.', 'en');
      setTimeout(() => startListening(), 2000);
    }
  };



  const sendTextMessage = () => {
    if (text.trim()) {
      handleUserMessage(text.trim());
      setText('');
    }
  };

  return (
    <div className="min-h-screen py-8 bg-gradient-to-br from-blue-50 to-indigo-100">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <Card variant="glass" className="shadow-xl">
          <CardContent className="p-8">
            <CardTitle className="mb-6 text-3xl font-bold text-center text-gray-800">
              Voice Health Assistant
            </CardTitle>

            {/* Voice Conversation Mode */}
            <div className="mb-8 text-center">
              <Button
                onClick={toggleConversationMode}
                className={`px-8 py-4 text-lg font-semibold rounded-full transition-all duration-300 ${
                  conversationMode
                    ? 'bg-red-500 hover:bg-red-600 text-white'
                    : 'bg-green-500 hover:bg-green-600 text-white'
                }`}
              >
                {conversationMode ? 'Stop Voice Chat' : 'Start Voice Chat'}
              </Button>
              <p className="mt-2 text-sm text-gray-600">
                {conversationMode ? 'Speak naturally - I\'ll listen and respond' : 'Click to talk with me about your health'}
              </p>
            </div>

            {/* Status Indicators */}
            <div className="flex justify-center gap-4 mb-6">
              <div className={`px-4 py-2 rounded-full text-sm font-medium ${
                isListening ? 'bg-blue-100 text-blue-800' : 'bg-gray-100 text-gray-600'
              }`}>
                {isListening ? 'üé§ Listening...' : 'üîá Not Listening'}
              </div>
            </div>

            {/* Manual Voice Input */}
            <div className="mb-6 text-center">
              <Button
                onClick={isListening ? stopListening : startListening}
                disabled={conversationMode}
                className={`px-6 py-3 text-lg font-semibold rounded-lg transition-all duration-300 ${
                  isListening
                    ? 'bg-green-500 hover:bg-green-600 text-white animate-pulse'
                    : 'bg-purple-500 hover:bg-purple-600 text-white'
                }`}
              >
                {isListening ? 'Stop Listening' : 'Listen Once'}
              </Button>
              <p className="mt-1 text-xs text-gray-500">Listen for one message</p>
            </div>

            {/* Error Display */}
            {error && (
              <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
                <p className="text-red-800 font-medium">‚ö†Ô∏è {error}</p>
              </div>
            )}

            {/* Text Input */}
            <div className="mb-6">
              <div className="flex gap-2">
                <input
                  type="text"
                  value={text}
                  onChange={(e) => setText(e.target.value)}
                  placeholder="Type your symptoms or question here..."
                  className="flex-1 px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent text-lg"
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      sendTextMessage();
                    }
                  }}
                />
                <Button
                  onClick={sendTextMessage}
                  disabled={!text.trim()}
                  className="px-6 py-3 bg-blue-500 hover:bg-blue-600 text-white rounded-lg font-medium"
                >
                  Ask
                </Button>
              </div>
            </div>

            {/* Chat Display */}
            <div className="bg-white/70 rounded-lg p-4 max-h-96 overflow-y-auto border border-gray-200">
              <h4 className="font-semibold mb-4 text-lg text-gray-800">Conversation</h4>
              {chat.length === 0 ? (
                <p className="text-gray-500 text-center py-8">
                  Start a conversation by speaking or typing your symptoms...
                </p>
              ) : (
                <div className="space-y-4">
                  {chat.map((m, i) => (
                    <div
                      key={i}
                      className={`p-3 rounded-lg ${
                        m.role === 'assistant'
                          ? 'bg-blue-50 border-l-4 border-blue-500'
                          : 'bg-green-50 border-l-4 border-green-500'
                      }`}
                    >
                      <div className="flex items-start gap-3">
                        <span className={`text-2xl ${m.role === 'assistant' ? 'ü§ñ' : 'üë§'}`}></span>
                        <div className="flex-1">
                          <span className="font-semibold text-sm text-gray-600 uppercase tracking-wide">
                            {m.role === 'assistant' ? 'Health Assistant' : 'You'}
                          </span>
                          <p className="mt-1 text-gray-800 leading-relaxed">{m.text}</p>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
