import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Card, CardContent, CardTitle } from '../components/Card';
import { login, register } from '../utils/api';
import { Button } from '../components/Button';
import { useToast } from '../components/Toast';

const Login = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { addToast } = useToast();
  const [email, setEmail] = useState('test@example.com');
  const [password, setPassword] = useState('pass');
  const [mode, setMode] = useState('login');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    // basic client-side validation
    const emailOk = /[^\s@]+@[^\s@]+\.[^\s@]+/.test(email);
    if (!emailOk) {
      setError('Please enter a valid email address');
      return;
    }
    if (String(password).length < 4) {
      setError('Password must be at least 4 characters');
      return;
    }
    setLoading(true);
    try {
      if (mode === 'register') {
        await register({ email, password });
        addToast({ title: 'Account created', description: 'You can now log in', variant: 'success' });
      }
      await login({ email, password });
      addToast({ title: 'Welcome back', description: 'Login successful', variant: 'success' });
      const to = (location.state && location.state.from && location.state.from.pathname) || '/';
      navigate(to, { replace: true });
    } catch (err) {
      const message = err.message || 'Something went wrong';
      setError(message);
      addToast({ title: 'Login failed', description: message, variant: 'error' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4 py-12 bg-gray-50">
      <div className="w-full max-w-md">
        <Card>
          <CardContent className="p-6">
            <CardTitle className="mb-6">{mode === 'login' ? 'Login' : 'Register'}</CardTitle>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                <input
                  type="email"
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                <input
                  type="password"
                  className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              {error && <div className="text-red-600 text-sm">{error}</div>}
              <Button type="submit" loading={loading} className="w-full">
                {mode === 'login' ? 'Login' : 'Create account'}
              </Button>
            </form>
            <div className="text-sm text-gray-600 mt-4">
              {mode === 'login' ? (
                <button className="text-primary-600" onClick={() => setMode('register')}>Create an account</button>
              ) : (
                <button className="text-primary-600" onClick={() => setMode('login')}>Already have an account? Login</button>
              )}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Login;
